import "./content.css";
import React, { useState } from "react";
import { useMutation } from "@apollo/react-hooks";
import { REMOVE_FLIGHT } from "../utils/mutations";
import { Accordion, Button, CardContent } from "semantic-ui-react";

const FlightCards = ({
  savedDetail,
  activeIndex,
  setActiveIndex,
  id,
  tripsData,
  setUserData,
  tripID,
}) => {
  const {
    _id,
    airline,
    departure,
    return: returning,
    duration,
    cost,
    people,
  } = savedDetail;
  const [removeFlight] = useMutation(REMOVE_FLIGHT);

  const handleClick = (e, { index }) => {
    const newIndex = activeIndex === index ? -1 : index;
    setActiveIndex(newIndex);
  };

  const handleDelete = async () => {
    await removeFlight({
      variables: {
        deleteField: {
          DeleteId: _id,
          TripId: tripID,
        },
      },
    });
    setActiveIndex(-1);
  };
  return (
    <>
      <section className="cont">
        <h1 className="SV">Saved Trip</h1>
        <div className="row">
          <article className="card fl-left">
            <section className="date">
              <time datetime="23th feb">
                <span>23</span>
                <span>feb</span>
              </time>
            </section>
            <section className="card-cont">
              <small>dj music event</small>
              <h3>music kaboom festivel</h3>
              <div className="even-date">
                <i className="fa fa-calendar"></i>
                <time>
                  <span> {departure}</span>
                  <span> {returning}</span>
                </time>
              </div>
              <div className="even-info">
                <i className="fa fa-map-marker"></i>
                <p>nexen square for people australia, sydney</p>
              </div>
              <a href="#">booked</a>
            </section>
          </article>
        </div>
      </section>
    </>
  );
};

export default FlightCards;
